const prompts = {
  "call_2_next": "Draft a sales call follow-up email for a lead who just completed a Qualification call (Sales Call 1). The next step is to schedule a 1-hour deep dive call to walk through a narrative analysis based on documents that the sales lead will provide or has provided. Your draft should focus on these next steps in the process: sharing discovery documents, executing an NDA, and locking in a time to connect (ONLY if that has not already been set during the meeting - based on the call summary).",
  "call_3_next": "Draft a follow-up email for a lead who just completed a 1-hour deep dive discovery call. The next step is to schedule a 30-minute call to review the draft Statement of Work (SOW). The email must mention reviewing the draft SOW during the next call, the goal of the upcoming review call, and a call to lock in timing for that meeting (ONLY if that has not already been set during the meeting - based on the call summary).",
  "sow_next": "Draft a follow-up email after a Statement of Work (SOW) review call, where the lead is expected to review the SOW, provide feedback, and confirm next steps for starting the partnership.",
  "undefined_next": "Draft a sales call follow-up email based on the following transcript."
}

function getTextById(values, targetId) {
  if (!Array.isArray(values)) return null;
  const found = values.find(v => v && v.id === targetId);
  return found?.text ?? null;
}

let prompt = prompts.undefined_next;

for (const item of $input.all()) {
  const cols = item?.json?.column_values ?? [];
  const nxt_stage = getTextById(cols, "deal_stage") || "";

  if (nxt_stage.includes("Call 2") || nxt_stage.includes("Diagnosis")) {
    prompt = prompts.call_2_next;
  } else if (nxt_stage.includes("Call 3") || nxt_stage.includes("SOW")) {
    prompt = prompts.call_3_next;
  } else if (nxt_stage.includes("Intent")) {
    prompt = prompts.sow_next;
  } else {
    prompt = prompts.undefined_next;
  }

  console.log(prompt);
}

return { json: { prompt } };
