parameters.jsCode:
// Assume the input text is in $json.text
const rawText = $input.first().json.body.candidates[0].content.parts[0].text;

// Parse the JSON string
let parsed;
try {
  parsed = JSON.parse(rawText);
} catch (e) {
  throw new Error("Invalid JSON: " + e.message);
}

// Return as n8n output
return [{ json: parsed }];

const links = $input.first().json.links

const cleaned = [];
const appId = '?id';

for (const link of links) {
  const href = link.href || '';
  if (href.includes('search')) continue;
  if (!href.includes(appId)) continue;
  const sanitized = Object.fromEntries(Object.entries(link).filter(([key]) => key !== 'jslog'));

  cleaned.push(sanitized);
}

const output = cleaned;
return output;

const links = $input.first().json.links;

const cleaned = [];
const appId = '/id';

for (const link of links) {
  const href = link.href || '';
  const testId = link['data-test-id'] || '';

  if (href.includes('search')) continue;
  if (testId.includes('other-language')) continue;

  if (!href.includes(appId)) continue;

  cleaned.push(link);
}

const output = cleaned;
return output;

