parameters.jsCode:
// Parse the atomizer output and prepare 5 separate payloads for each framework path
const rawOutput = $input.first().json.output;

// More aggressive cleaning - extract JSON from any surrounding text
let cleanOutput = rawOutput;

// Remove markdown code blocks
cleanOutput = cleanOutput.replace(/```json\n?/g, '').replace(/```\n?/g, '');

// Find the first { and last } to extract just the JSON object
const firstBrace = cleanOutput.indexOf('{');
const lastBrace = cleanOutput.lastIndexOf('}');

if (firstBrace !== -1 && lastBrace !== -1) {
  cleanOutput = cleanOutput.substring(firstBrace, lastBrace + 1);
}

cleanOutput = cleanOutput.trim();

const atomizerOutput = JSON.parse(cleanOutput);
const newsletterText = $input.first().json.chatInput;

// Convert the old format (idea_1, idea_2, idea_3) to array format
const ideas = [
  {
    number: 1,
    text: atomizerOutput.idea_1.standalone_content,
    title: atomizerOutput.idea_1.title,
    suggested_frameworks: atomizerOutput.idea_1.potential_uses || []
  },
  {
    number: 2,
    text: atomizerOutput.idea_2.standalone_content,
    title: atomizerOutput.idea_2.title,
    suggested_frameworks: atomizerOutput.idea_2.potential_uses || []
  },
  {
    number: 3,
    text: atomizerOutput.idea_3.standalone_content,
    title: atomizerOutput.idea_3.title,
    suggested_frameworks: atomizerOutput.idea_3.potential_uses || []
  }
];

const frameworks = [
  {
    type: "borrowed_story",
    instruction: "Select the ONE idea from the 3 options that works best with external examples, case studies, or historical parallels. This framework requires stories from other companies, people, or historical events to make a point.",
    needs_research: true
  },
  {
    type: "curation",
    instruction: "Select the ONE idea from the 3 options that can showcase multiple specimens, examples, tools, or curated items. This framework requires the ability to filter signal from noise and present a valuable list.",
    needs_research: true
  },
  {
    type: "listicle",
    instruction: "Select the ONE idea from the 3 options that breaks down into clear, actionable numbered points. This framework turns wisdom into digestible list-based pieces.",
    needs_research: false
  },
  {
    type: "opinion",
    instruction: "Select the ONE idea from the 3 options that allows for a strong, potentially controversial stance or sharp observation. This framework requires a clear point of view.",
    needs_research: false
  },
  {
    type: "personal_story",
    instruction: "Select the ONE idea from the 3 options that connects best to personal narrative or lived experience. This framework makes a point through personal storytelling.",
    needs_research: false
  }
];

return frameworks.map(fw => ({
  json: {
    framework_type: fw.type,
    all_ideas: ideas,
    newsletter_text: newsletterText,
    newsletter_summary: atomizerOutput.extraction_summary?.coverage || "Newsletter summary",
    selection_instruction: fw.instruction,
    needs_research: fw.needs_research,
    timestamp: new Date().toISOString()
  }
}));

