// Store Borrowed Story output in structured format
const input = $input.first().json;

const selectorOutput = JSON.parse(input.output || '{}');
const writerOutput = JSON.parse(input.output || '{}');
const editorOutput = JSON.parse(input.output || '{}');

return [{
  json: {
    framework: "borrowed_story",
    selected_idea: selectorOutput.selected_idea_text,
    selection_reasoning: selectorOutput.selection_reasoning,
    research_status: input.research_status || "success",
    draft_post: writerOutput.post_content,
    draft_word_count: writerOutput.word_count,
    sources_used: writerOutput.sources_used,
    final_post: editorOutput.edited_post,
    quality_scores: editorOutput.quality_scores,
    final_score: editorOutput.final_score,
    changes_made: editorOutput.changes_made,
    editor_notes: editorOutput.editor_notes,
    timestamp: new Date().toISOString()
  }
}];

