// Store Opinion output
const input = $input.first().json;

const selectorOutput = JSON.parse(input.output || '{}');
const writerOutput = JSON.parse(input.output || '{}');
const editorOutput = JSON.parse(input.output || '{}');

return [{
  json: {
    framework: "opinion",
    selected_idea: selectorOutput.selected_idea_text,
    selection_reasoning: selectorOutput.selection_reasoning,
    draft_post: writerOutput.post_content,
    draft_word_count: writerOutput.word_count,
    stance: writerOutput.stance,
    final_post: editorOutput.edited_post,
    quality_scores: editorOutput.quality_scores,
    final_score: editorOutput.final_score,
    changes_made: editorOutput.changes_made,
    editor_notes: editorOutput.editor_notes,
    timestamp: new Date().toISOString()
  }
}];

// Store Personal Story output
const input = $input.first().json;

const selectorOutput = JSON.parse(input.output || '{}');
const writerOutput = JSON.parse(input.output || '{}');
const editorOutput = JSON.parse(input.output || '{}');

return [{
  json: {
    framework: "personal_story",
    selected_idea: selectorOutput.selected_idea_text,
    selection_reasoning: selectorOutput.selection_reasoning,
    draft_post: writerOutput.post_content,
    draft_word_count: writerOutput.word_count,
    story_moment: writerOutput.story_moment,
    final_post: editorOutput.edited_post,
    quality_scores: editorOutput.quality_scores,
    final_score: editorOutput.final_score,
    changes_made: editorOutput.changes_made,
    editor_notes: editorOutput.editor_notes,
    timestamp: new Date().toISOString()
  }
}];

// Analyze which ideas were selected and flag duplications
const allOutputs = $input.all();

const ideaUsage = {};
const analysis = {
  posts: [],
  duplications: [],
  idea_usage_summary: {}
};

allOutputs.forEach(item => {
  const data = item.json;
  const framework = data.framework;
  const selectedIdea = data.selected_idea;

  // Track idea usage
  if (!ideaUsage[selectedIdea]) {
    ideaUsage[selectedIdea] = [];
  }
  ideaUsage[selectedIdea].push(framework);

  analysis.posts.push({
    framework: framework,
    selected_idea: selectedIdea,
    final_score: data.final_score
  });
});

// Identify duplications
Object.keys(ideaUsage).forEach(idea => {
  const frameworks = ideaUsage[idea];
  analysis.idea_usage_summary[idea] = frameworks.length;

  if (frameworks.length > 1) {
    analysis.duplications.push({
      idea: idea.substring(0, 100) + '...',
      used_by: frameworks,
      count: frameworks.length
    });
  }
});

return [{
  json: {
    all_posts: allOutputs.map(item => item.json),
    analysis: analysis,
    has_duplications: analysis.duplications.length > 0,
    total_posts: allOutputs.length,
    timestamp: new Date().toISOString()
  }
}];

// Format all content for Google Doc
const input = $input.first().json;
const allPosts = input.all_posts;
const analysis = input.analysis;

let docContent = `# Newsletter Transformation - ${new Date().toLocaleString()}\n\n`;

if (input.has_duplications) {
  docContent += `⚠️ **Note**: Some ideas were selected by multiple frameworks:\n`;
  analysis.duplications.forEach(dup => {
    docContent += `- "${dup.idea}" used by: ${dup.used_by.join(', ')}\n`;
  });
  docContent += `\n---\n\n`;
}

allPosts.forEach((post, index) => {
  docContent += `## ${index + 1}. ${post.framework.replace('_', ' ').toUpperCase()} POST\n\n`;
  docContent += `**Framework**: ${post.framework}\n`;
  docContent += `**Quality Score**: ${post.final_score}/10\n`;
  docContent += `**Selected Idea**: ${post.selected_idea}\n`;
  docContent += `**Reasoning**: ${post.selection_reasoning}\n\n`;

  if (post.research_status) {
    docContent += `**Research Status**: ${post.research_status}\n\n`;
  }

  docContent += `### Draft Version\n\n`;
  docContent += `${post.draft_post}\n\n`;
  docContent += `*Word Count: ${post.draft_word_count}*\n\n`;

  docContent += `### Final Edited Version\n\n`;
  docContent += `${post.final_post}\n\n`;

  docContent += `**Quality Breakdown**:\n`;
  docContent += `- Hook: ${post.quality_scores.hook}/10\n`;
  docContent += `- Body: ${post.quality_scores.body}/10\n`;
  docContent += `- Takeaway: ${post.quality_scores.takeaway}/10\n`;
  docContent += `- Voice Alignment: ${post.quality_scores.voice_alignment}/10\n\n`;

  docContent += `**Changes Made**: ${post.changes_made.join(', ')}\n\n`;
  docContent += `**Editor Notes**: ${post.editor_notes}\n\n`;
  docContent += `---\n\n`;
});

docContent += `## Workflow Metadata\n\n`;
docContent += `- Total Posts Generated: ${allPosts.length}\n`;
docContent += `- Ideas Analyzed: 3\n`;
docContent += `- Frameworks Used: Borrowed Story, Curation, Listicle, Opinion, Personal Story\n`;
docContent += `- Generated: ${input.timestamp}\n`;

return [{
  json: {
    formatted_content: docContent,
    metadata: {
      total_posts: allPosts.length,
      average_score: (allPosts.reduce((sum, p) => sum + p.final_score, 0) / allPosts.length).toFixed(2),
      has_duplications: input.has_duplications
    }
  }
}];
