parameters.inputSchema:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "OfferExtractionSchema",
  "type": "object",
  "properties": {
    "game": {
      "type": "string",
      "description": "Exact name of the game/app/platform being advertised. Use 'Not Specified' if unavailable."
    },
    "offers": {
      "type": "array",
      "description": "List of specific actions the user must take.",
      "items": {
        "type": "string"
      }
    },
    "payout_structure": {
      "type": "string",
      "description": "Clear payout or reward structure. Use 'Not Specified' if unavailable."
    }
  },
  "required": ["game", "offers", "payout_structure"],
  "additionalProperties": false
}

parameters.jsonBody:
={
    "contents": [
        {
            "role": "user",
            "parts": [
                {
                    "fileData": {
                        "fileUri": "{{ $json.file.uri }}",
                        "mimeType": "{{ $json.file.mimeType }}"
                    }
                },
                {
                    "text": "Describe in detail what is visually happening in the video, including key elements, actions, colors, and branding. Note the style, tone, and any notable creative techniques being used."
                }
            ]
        }
    ],
    "generationConfig": {
        "temperature": 1.4,
        "topK": 40,
        "topP": 0.95,
        "maxOutputTokens": 8192,
        "responseModalities": ["Text"]
    }
}

